export type State = {
  speed: number
  text: string
  count: number
}

export type Action =
  | { type: 'DELAY'; payload: number }
  | { type: 'TYPE'; payload: string; speed: number }
  | { type: 'DELETE'; payload: string; speed: number }
  | { type: 'COUNT' }

export function reducer(state: State, action: Action): State {
  switch (action.type) {
    case 'TYPE':
      return {
        ...state,
        speed: action.speed,
        text: action.payload?.substring(0, state.text.length + 1)
      }
    case 'DELAY':
      return {
        ...state,
        speed: action.payload
      }
    case 'DELETE':
      return {
        ...state,
        speed: action.speed,
        text: action.payload?.substring(0, state.text.length - 1)
      }
    case 'COUNT':
      return {
        ...state,
        count: state.count + 1
      }
    default:
      return state
  }
}
import { useCallback, useEffect, useRef, useReducer } from 'react'
import { reducer } from '../reducer'

export type TypewriterProps = {
  /** Callback Function that is triggered when loops are completed. available if loop is > `0` */
  onLoopDone?: () => void
  /** Callback Function that is triggered while typing with `typed` words count passed */
  onType?: (count: number) => void
  /** Callback Function that is triggered while deleting */
  onDelete?: () => void
  /** Callback Function that is triggered on typing delay */
  onDelay?: () => void
  /** Array of strings holding the words */
  words: string[]
  /** Control how many times to run. `0 | false` to run infinitely */
  loop?: number | boolean
  /** Character typing speed in Milliseconds */
  typeSpeed?: number
  /** Character deleting speed in Milliseconds */
  deleteSpeed?: number
  /** Delay time between the words in Milliseconds */
  delaySpeed?: number
}

export type TypewriterHelper = {
  /** `true` if currently typing */
  isType: boolean
  /** `true` if on delay */
  isDelay: boolean
  /** `true` if currently deleting */
  isDelete: boolean
  /** `true` if all loops are done */
  isDone: boolean
}

export const useTypewriter = ({
  words = ['Hello World!', 'This is', 'a simple Typewriter'],
  loop = 1,
  typeSpeed = 80,
  deleteSpeed = 50,
  delaySpeed = 1500,
  onLoopDone,
  onType,
  onDelete,
  onDelay
}: TypewriterProps): [string, TypewriterHelper] => {
  const [{ speed, text, count }, dispatch] = useReducer(reducer, {
    speed: typeSpeed,
    text: '',
    count: 0
  })

  // Refs
  const loops = useRef(0)
  const isDone = useRef(false)
  const isDelete = useRef(false)
  const isType = useRef(false)
  const isDelay = useRef(false)

  const handleTyping = useCallback(() => {
    const index = count % words.length
    const fullWord = words[index]

    if (!isDelete.current) {
      dispatch({ type: 'TYPE', payload: fullWord, speed: typeSpeed })
      isType.current = true

      if (text === fullWord) {
        dispatch({ type: 'DELAY', payload: delaySpeed })
        isType.current = false
        isDelay.current = true

        setTimeout(() => {
          isDelay.current = false
          isDelete.current = true
        }, delaySpeed)

        if (loop > 0) {
          loops.current += 1
          if (loops.current / words.length === loop) {
            isDelay.current = false
            isDone.current = true
          }
        }
      }
    } else {
      dispatch({ type: 'DELETE', payload: fullWord, speed: deleteSpeed })
      if (text === '') {
        isDelete.current = false
        dispatch({ type: 'COUNT' })
      }
    }

    if (isType.current) {
      if (onType) onType(loops.current)
    }

    if (isDelete.current) {
      if (onDelete) onDelete()
    }

    if (isDelay.current) {
      if (onDelay) onDelay()
    }
  }, [
    count,
    delaySpeed,
    deleteSpeed,
    loop,
    typeSpeed,
    words,
    text,
    onType,
    onDelete,
    onDelay
  ])

  useEffect(() => {
    const typing = setTimeout(handleTyping, speed)

    if (isDone.current) clearTimeout(typing)

    return () => clearTimeout(typing)
  }, [handleTyping, speed])

  useEffect(() => {
    if (!onLoopDone) return

    if (isDone.current) {
      onLoopDone()
    }
  }, [onLoopDone])

  return [
    text,
    {
      isType: isType.current,
      isDelay: isDelay.current,
      isDelete: isDelete.current,
      isDone: isDone.current
    }
  ]
}
function styleInject(css, ref) {
  if ( ref === void 0 ) ref = {};
  var insertAt = ref.insertAt;

  if (!css || typeof document === 'undefined') { return; }

  var head = document.head || document.getElementsByTagName('head')[0];
  var style = document.createElement('style');
  style.type = 'text/css';

  if (insertAt === 'top') {
    if (head.firstChild) {
      head.insertBefore(style, head.firstChild);
    } else {
      head.appendChild(style);
    }
  } else {
    head.appendChild(style);
  }

  if (style.styleSheet) {
    style.styleSheet.cssText = css;
  } else {
    style.appendChild(document.createTextNode(css));
  }
}

export default styleInject;
import { ReactNode, memo } from 'react'
import styles from '../styles.module.css'

export type CursorProps = {
  /** Enable cursor blinking animation */
  cursorBlinking?: boolean
  /** Change cursor style */
  cursorStyle?: ReactNode
  /** Change cursor color */
  cursorColor?: string
}

const MemoizedCursor = ({
  cursorBlinking = true,
  cursorStyle = '|',
  cursorColor = 'inherit'
}: CursorProps): JSX.Element => {
  return (
    <span
      style={{ color: cursorColor }}
      className={`${styles.blinkingCursor} ${
        cursorBlinking ? styles.blinking : ''
      }`}
    >
      {cursorStyle}
    </span>
  )
}

export const Cursor = memo(MemoizedCursor)
import { TypewriterProps, useTypewriter } from '../hooks/useTypewriter'
import { Cursor, CursorProps } from './Cursor'

type ComponentProps = {
  /** Show / Hide the cursor */
  cursor?: boolean
} & TypewriterProps &
  CursorProps

/**
 * A Simple React Component for adding a nice Typewriter effect to your project.
 */
export const Typewriter = ({
  words = ['Hello World!', 'This is', 'a simple Typewriter'],
  loop = 1,
  typeSpeed = 80,
  deleteSpeed = 50,
  delaySpeed = 1500,
  cursor = false,
  cursorStyle = '|',
  cursorColor = 'inherit',
  cursorBlinking = true,
  onLoopDone,
  onType,
  onDelay,
  onDelete
}: ComponentProps): JSX.Element => {
  const [text] = useTypewriter({
    words,
    loop,
    typeSpeed,
    deleteSpeed,
    delaySpeed,
    onLoopDone,
    onType,
    onDelay,
    onDelete
  })

  return (
    <>
      <span>{text}</span>
      {cursor && (
        <Cursor
          cursorStyle={cursorStyle}
          cursorColor={cursorColor}
          cursorBlinking={cursorBlinking}
        />
      )}
    </>
  )
}
